mapper.select:
/*AdminMng_SQL.selectMenuNmDupliChk*/
SELECT COUNT(*)
  FROM TB_ADMIN_MENU_INFO
WHERE 1=1
    AND MENU_NM = #{uniqueKey}
    AND MENU_DEL_YN = 'N'

/*AdminMng_SQL.selectUniqueDupliChk*/
SELECT COUNT(*)
  FROM TB_COMMON_INFO
WHERE 1=1
    AND COMM_CD = #{uniqueKey}
    AND COMM_DEL_YN = 'N'

/*AdminMng_SQL.selectUnitCdDupliChk*/
SELECT COUNT(*)
  FROM TB_ADMIN_UNIT_INFO
WHERE 1=1
    AND ADMIN_UNIT_CD = #{uniqueKey}
    AND ADMIN_UNIT_DEL_YN = 'N'
